import react from 'react'
import Layout from '../pages/layout'
const API_KEY = "df05602edb5dcfcf3f9bf5e033234419"

class home extends React.Component {
	constructor() {
		super()
		this.state = {

		}
	}

	getWeather = async(e) => {
		// e.preventDefault();
		const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=London,UK&appid=${API_KEY}&units=metric`)
		const data = await api_call.json()
		console.log(data)
	}
	render () {
		return (
			<Layout>
				<div className="py-5">
					<div className="col-5 mx-auto">
						<form onSubmit={this.getWeather}>
							<div className="mb-2">
								<input placeholder="choose location" className="form-control" />
							</div>
							<div>
								<button className="btn btn-sm btn-outline-info btn-block">Search</button>
							</div>
						</form>
					</div>
				</div>
			</Layout>
		)
	}
}

export default home
