import react from 'react'
import Layout from '../pages/layout'
import axios from 'axios'
const API_KEY = "df05602edb5dcfcf3f9bf5e033234419"

const styles = {
	background: '#e44534',
	height: '100vh'
}
const form ={
	background: '#273648',
	height: '50vh'
}

class home extends React.Component {
	constructor() {
		super()
		this.state = {
			location: '',
			data_weather: '',
			data_country: '',
			data_location: '',
			data_conditions: []

		}
	}

	handleInput(value) {
		this.setState({[value.target.name]: value.target.value})
	}

	getWeather () {
		if(this.state.location != '') {
			const location = this.state.location
			axios.get(`http://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${API_KEY}&units=metric`).then(res=> {
				console.log(res)
				this.setState({
					data_location: res.data,
					data_weather: res.data.main,
					data_country: res.data.sys,
					data_conditions: res.data.weather
				})
			})
		}
	}
	render () {
		return (
			<Layout>
				<div className="py-5" style={styles}>
					<div>
						<div className="col-5 mx-auto p-5" style={form}>
							<div className=" d-flex">
								<div className="mb-2">
									<input placeholder="choose location" className="form-control" name="location" onChange={this.handleInput.bind(this)} />
								</div>
								<div className="align-self-center">
									<button type="button" className="btn btn-sm btn-outline-info btn-block" onClick={()=>this.getWeather()}>Search</button>
								</div>
							</div>
							{this.state.data_location != '' ?
								<div className="py-3">
									<div>
										<h6>Location : {this.state.data_location.name}, {this.state.data_country.country}</h6>
									</div>
									<div>
										<h6>Temperature : {this.state.data_weather.temp} 'C</h6>
									</div>
									<div>
										<h6>Humadity : {this.state.data_weather.humidity}</h6>
									</div>
									{this.state.data_conditions.map((cond, i)=> {
										return (
										<div key={i}>
											<h6>Conditions : {cond.description}</h6>
										</div>
										)
									})}
								</div>
								: null
							}
						</div>
					</div>
				</div>
			</Layout>
		)
	}
}

export default home
