import react from 'react'

class Signup extends React.Component {
	constructor() {
		super()
		this.state = {
			email: '',
			password: ''
		}
	}

	handleInput(value) {
		this.setState({[value.target.name]: value.target.value})
	}

	submit() {
		if(this.state.email != '' && this.state.password != '') {
			console.log(this.state.email, this.state.password)
			var user = {
				email: this.state.email,
				password: this.state.password
			}
			localStorage.setItem('user', JSON.stringify(user))
		}
	}
	render () {
		return (
			<div>
				<div className="col-5 mx-auto">
					<div className="card p-5">
						<h4 className="text-center">Signup</h4>
						<hr className="m-0" />
						<div className="py-5">
							<div className="mb-2">
								<input placeholder="Email" className="form-control" name="email" onChange={this.handleInput.bind(this)} />
							</div>
							<div className="mb-2">
								<input placeholder="Password" className="form-control" name="password" onChange={this.handleInput.bind(this)} />
							</div>
							<div>
								<button className="btn btn-sm btn-info btn-block" onClick={()=> this.submit()}>submit</button>
							</div>
						</div>
					</div>
				</div>
			</div>
		)
	}
}

export default Signup;
