import react from 'react'

class Login extends React.Component {
	constructor() {
		super()
		this.state = {
			email: '',
			password: '',
			alert: false
		}
	}

	handleInput(value) {
		this.setState({[value.target.name]: value.target.value})
	}
	reset() {
		this.setState({
			email: '',
			password: ''
		});
		this.refs.email.value = ''
		this.refs.password.value = ''
	}

	submit() {
		if(this.state.email != '' && this.state.password != ''){
			var user = JSON.parse(localStorage.getItem('user'))
			console.log(user)
			for (var i = 0; i < user.length; i++) {
				if(user[i].email == this.state.email && user[i].password == this.state.password) {
					this.setState({alert: false});
					this.reset()
					window.location.href = '/home'
				}else {
					this.reset()
					this.setState({alert: true})

				}
			}
		}
	}
	render () {
		return (
			<div>
				<div className="col-5 mx-auto">
					<div className="card p-5">
						<h4 className="text-center">Login</h4>
						<hr className="m-0" />
						{this.state.alert == true ?
							<div className="text-center m-2">
								<h6 className="text-danger">Email atau Password anda salah. Silahkan coba lagi</h6>
							</div>
							: null
						}
						<div className="py-5">
							<div className="mb-2">
								<input placeholder="Email" className="form-control" name="email" onChange={this.handleInput.bind(this)} ref="email" />
							</div>
							<div className="mb-2">
								<input placeholder="Password" className="form-control" name="password" onChange={this.handleInput.bind(this)} ref="password"/>
							</div>
							<div>
								<button className="btn btn-sm btn-info btn-block" onClick={()=> this.submit()}>submit</button>
							</div>
						</div>
					</div>
				</div>
			</div>
		)
	}
}

export default Login;
